import React, {useState} from 'react';

export default function Todo({id, name, completed, onCheckChange, onClickDelete, onClickSave}) {
    const [isEditing, setIsEditing] = useState(false);

    const editingTemplate = (
      <form className="stack-small">
        <div className="form-group">
          <label className="todo-label" htmlFor={id}>
            New name for {name}
          </label>
          <input type="text" className="todo-text" id={id} />
        </div>
        <div className="btn-group">
          <button type="button" className="btn todo-cancel" onClick={() => setIsEditing(false)}>
            Cancel 
            <span className="visually-hidden">renaming {name}</span>
          </button>
          <button type="submit" className="btn btn__primary todo-edit" onClick={() => onClickSave(id, input)}>
            Save
            <span className="visually-hidden">New name for {name}</span>
          </button>
        </div>
      </form>
    );

    const viewTemplate = (
      <div className="stack-small">
        <div className="c-cb">
          <input id={id} type="checkbox" defaultChecked={completed} onChange={onCheckChange} />
          <label className="todo-label" htmlFor={id}>
            {name}
          </label>
        </div>
        <div className="btn-group">
          <button type="button" className="btn" onClick={() => setIsEditing(true)}>
            Edit <span className="visually-hidden">{name}</span>
          </button>
          <button type="button" className="btn btn__danger" onClick={() => onClickDelete(id)}>
            Delete <span className="visually-hidden">{name}</span>
          </button>
        </div>
      </div>
    );

    return <li className="todo">{isEditing ? editingTemplate : viewTemplate}</li>;
}
