import React, {useState} from 'react';

export default function Todo({id, name, completed, onCheckChange, onClickDelete, onClickEdit}) {
    const [isEditing, setIsEditing] = useState(false);

    return (
        <li className="todo stack-small">
          <div className="c-cb">
            <input id={id} type="checkbox" defaultChecked={completed} onChange={onCheckChange} />
            <label className="todo-label" htmlFor={id}>
              {name}
            </label>
          </div>
          <div className="btn-group">
            <button type="button" className="btn">
              Edit <span className="visually-hidden">{name}</span>
            </button>
            <button type="button" className="btn btn__danger" onClick={() => onClickDelete(id)}>
              Delete <span className="visually-hidden">{name}</span>
            </button>
          </div>
        </li>
    );
}
