import React, {useState} from 'react';

export default function Todo({id, name, completed, onCheckChange, onClickDelete, onClickEdit, onClickSave, onEdit}) {
    const [isEditting, setIsEditting] = useState(false);

    const handleOnClickToggle = (e) => {
      setIsEditting(!isEditting);
    }

    return (
        <li className="todo stack-small">
          <div className="c-cb">
            <input id={id} type="checkbox" defaultChecked={completed} onChange={onCheckChange} />
            {isEditting ? (
              <input type="text" value={name} onChange={onEdit} />
            ) : (<label className="todo-label" htmlFor={id}>
              {name}
            </label>)}
          </div>
          <div className="btn-group">
            {isEditing ? (
              <button type="button" className="btn" onClick={onClickSave}>
              Save <span className="visually-hidden">{name}</span>
            </button>
            ) : (<button type="button" className="btn" onClick={onClickEdit}>
              Edit <span className="visually-hidden">{name}</span>
            </button>)}

            <button type="button" className="btn btn__danger" onClick={onClickDelete}>
              Delete <span className="visually-hidden">{name}</span>
            </button>
          </div>
        </li>
    );
}
